import React from "react";
import { useParams } from "react-router-dom";
import { restaurantData } from "./data/restaurants";
import "./RestaurantPage.css";
import Header from "./Header";
import Sidebar from "./Sidebar";

function RestaurantPage() {
  const { id } = useParams();
  const restaurant = restaurantData[id];

  if (!restaurant) {
    return <h2>Restaurant Not Found</h2>;
  }

    return (
    <div className="restaurant-layout">
      <Sidebar />

      <div className="main-content">
        <Header />

        <div className="restaurant-page">
          {/* Header Section: Restaurant Name + Search Bar */}
          <div className="restaurant-header-section">
            <h1 className="restaurant-title">{restaurant.name}</h1>
            <div className="restaurant-search">
              <span className="search-icon">üîç</span>
              <input 
                type="text" 
                placeholder={`Search ${restaurant.name}...`} 
                className="search-bar"
              />
            </div>
          </div>

          {/* Store Info & Delivery Options Section */}
          <div className="restaurant-info-container">
            {/* Left: Store Info */}
            <div className="store-info">
              <h2 className="store-info-title">Store Info</h2>
              {restaurant.dashPass && <p className="dash-pass">üöÄ DashPass</p>}
              <p className="status">
                ‚è∞ <span className="open-status">{restaurant.status}</span> ‚Ä¢ Closes at {restaurant.closingTime}
              </p>
              <p>‚≠ê {restaurant.rating} ({restaurant.reviews}) ‚Ä¢ {restaurant.distance}</p>
              <p>{restaurant.price} ‚Ä¢ {restaurant.cuisine}</p>
              <button className="see-more">See More</button>
            </div>

            {/* Right: Delivery Options & Pricing */}
            <div className="delivery-options">
              <div className="buttons">
                <button className="delivery-option active">Delivery</button>
                <button className="pickup-option">Pickup</button>
                <button className="group-order">Group Order</button>
              </div>

              <div className="delivery-fee-container">
                <div className="delivery-fee">
                  <span className="fee-price">${restaurant.fee}</span>
                  <span className="fee-text">delivery fee</span>
                  <p className="pricing-text">pricing & fees ‚ÑπÔ∏è</p>
                </div>
                <div className="delivery-time">
                  <p>{restaurant.deliveryTime}</p>
                  <span className="time-text">delivery time</span>
                </div>
              </div>
            </div>
          </div> 
        </div>
      </div>
    </div>
  );
}

export default RestaurantPage;
