import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import "./OrderTracking.css";

// ✅ Icons for Delivery & User
const deliveryIcon = new L.Icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/3203/3203656.png",
  iconSize: [40, 40],
  iconAnchor: [20, 40],
});

const userIcon = new L.Icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/3177/3177440.png",
  iconSize: [40, 40],
  iconAnchor: [20, 40],
});

// ✅ Updates Map View to Follow User Location
const UpdateMapView = ({ userLocation }) => {
  const map = useMap();

  useEffect(() => {
    if (userLocation) {
      console.log("Updating map view to:", userLocation);
      map.setView(userLocation, 13);
    }
  }, [userLocation, map]);

  return null;
};

function OrderTracking() {
  const [userLocation, setUserLocation] = useState(null);
  const deliveryLocation = [37.7749, -122.4194]; // Example delivery location

  // ✅ Track real-time location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.watchPosition(
        (position) => {
          setUserLocation([position.coords.latitude, position.coords.longitude]);
        },
        (error) => {
          console.error("Error getting location:", error);
        }
      );
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  }, []);

  return (
    <div className="order-tracking-page">
      <h1 className="order-title">🚀 Your Order Has Been Submitted!</h1>
      <p className="order-subtext">Your Dasher is on the way to deliver your food.</p>

      <div className="map-container">
        <MapContainer center={deliveryLocation} zoom={13} className="map">
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

          {/* ✅ Pass userLocation correctly */}
          {userLocation && <UpdateMapView userLocation={userLocation} />}

          {/* 📦 Delivery Location Marker */}
          <Marker position={deliveryLocation} icon={deliveryIcon}>
            <Popup>📦 Order is being delivered here!</Popup>
          </Marker>

          {/* 📍 User's Real-Time Location Marker */}
          {userLocation && (
            <Marker position={userLocation} icon={userIcon}>
              <Popup>📍 You are here</Popup>
            </Marker>
          )}
        </MapContainer>
      </div>

      <div className="order-details">
        <h2>📦 Order Details</h2>
        <p><strong>Order ID:</strong> #123456789</p>
        <p><strong>Delivery ETA:</strong> 12:54 PM</p>
        <p><strong>Dasher:</strong> John Doe (+1 888-888-8888)</p>
      </div>
    </div>
  );
}

export default OrderTracking;
