import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import "./OrderTracking.css";

// ‚úÖ Icons for Delivery & User
const deliveryIcon = new L.Icon({
  iconUrl: "https://example.com/delivery-icon.png", // Replace with your URL
  iconSize: [40, 40],
  iconAnchor: [20, 40],
});

const userIcon = new L.Icon({
  iconUrl: "https://leafletjs.com/examples/custom-icons/leaf-green.png", // Replace with your own icon
  iconSize: [40, 40],
  iconAnchor: [20, 40],
});

// ‚úÖ Updates Map View to Follow User Location
const UpdateMapView = ({ userLocation }) => {
  const map = useMap();

  useEffect(() => {
    if (userLocation) {
      map.setView(userLocation, map.getZoom());
    }
  }, [userLocation, map]);

  return null;
};

const OrderTracking = () => {
  const [userLocation, setUserLocation] = useState(null);
  const deliveryLocation = [37.7749, -122.4194];

  // Track real-time user location
  useEffect(() => {
    if (navigator.geolocation) {
      const watchId = navigator.geolocation.watchPosition(
        (position) => {
          setUserLocation([
            position.coords.latitude,
            position.coords.longitude,
          ]);
        },
        (error) => console.error("Error tracking location", error),
      );
      return () => navigator.geolocation.clearWatch(watchId);
    }
  }, []);

  return (
    <div className="order-tracking">
      <h1>üöÄ Your Order Has Been Submitted!</h1>
      <p>Your Dasher is on the way to deliver your food.</p>

      <MapContainer
        className="map"
        center={userLocation || deliveryLocation}
        zoom={15}
        style={{ height: "400px", width: "100%" }}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        <Marker position={deliveryLocation} icon={deliveryIcon}>
          <Popup>üì¶ Order will arrive here!</Popup>
        </Marker>

        {userLocation && (
          <>
            <UpdateMapView userLocation={userLocation} />
            <Marker position={userLocation} icon={userIcon}>
              <Popup>üìç You are here</Popup>
            </Marker>
          </>
        )}
      </MapContainer>

      <div className="order-details">
        <p><strong>Order ID:</strong> #123456789</p>
        <p><strong>Delivery ETA:</strong> 12:54 PM</p>
        <p><strong>Dasher:</strong> John Doe (+1 888-888-8888)</p>
      </div>
    </div>
  );
};

export default OrderTracking;
