import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch, faMapMarkerAlt, faBell, faShoppingCart } from "@fortawesome/free-solid-svg-icons";
import "./Header.css"; 
import Cart from "./Cart"; 

function Header() {
  const [isCartOpen, setIsCartOpen] = useState(false);

  const toggleCart = () => {
    setIsCartOpen(!isCartOpen);
  };

  return (
    <>
      <header className="header">
        <div className="logo">
          <img src="https://1000logos.net/doordash-logo/" alt="DroneDash" />
        </div>

        <div className="search-bar">
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
          <input type="text" placeholder="Search DoorDash" />
        </div>

        <div className="location">
          <FontAwesomeIcon icon={faMapMarkerAlt} className="location-icon" />
          <span>123 North Avenue</span>
        </div>

        <div className="delivery-options">
          <button className="active">Delivery</button>
        </div>

        <div className="icons">
          <div className="notification">
            <FontAwesomeIcon icon={faBell} />
            <span className="badge">4</span>
          </div>
          <div className="cart" onClick={toggleCart}>
            <FontAwesomeIcon icon={faShoppingCart} />
            <span className="cart-badge">0</span>
          </div>
        </div>
      </header>

      {/* Cart Overlay (Only Visible When Cart is Open) */}
      <div className={`cart-overlay ${isCartOpen ? "open" : ""}`} onClick={toggleCart}></div>

      {/* Cart Sidebar - Always Present But Moves in/out */}
      <div className={`cart-container ${isCartOpen ? "open" : ""}`}>
        <button className="cart-close" onClick={toggleCart}>âœ–</button>

        {/* Show Blank Cart When Closed */}
        {!isCartOpen && <p className="cart-placeholder">Your Cart is Empty</p>}

        {/* Show Full Cart When Open */}
        {isCartOpen && <Cart />}
      </div>
    </>

  );
}

export default Header;
